@jit
def list_add(a,b):
    c = []
    for i in range(len(a)):
        c.append(a[i]+b[i])
    return c
@jit
def add_data(output, input_list, keyword):
    if(keyword not in output.keys()):
        output[keyword] = input_list
    else:
        output[keyword] = list_add(output[keyword],input_list)
    return output

